// frontend/src/admin/pages/MessagesPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/MessagesPage.css';

const MessagesPage = () => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedMessage, setSelectedMessage] = useState(null);

  // Fetch messages
  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const token = localStorage.getItem('token'); // Assuming you store auth token in localStorage

        const response = await axios.get('/api/messages', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });

        setMessages(response.data.data);
        setLoading(false);
      } catch (error) {
        setError(error.response?.data?.message || 'Error fetching messages');
        setLoading(false);
      }
    };

    fetchMessages();
  }, []);

  // Mark message as read
  const markAsRead = async (id) => {
    try {
      const token = localStorage.getItem('token');

      await axios.patch(`/api/messages/${id}/read`, {}, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      // Update local state
      setMessages(messages.map(message => 
        message._id === id ? { ...message, isRead: true } : message
      ));

    } catch (error) {
      console.error('Error marking message as read:', error);
    }
  };

  // Delete message
  const deleteMessage = async (id) => {
    if (!window.confirm('Are you sure you want to delete this message?')) return;

    try {
      const token = localStorage.getItem('token');

      await axios.delete(`/api/messages/${id}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      // Update local state
      setMessages(messages.filter(message => message._id !== id));

      // Close message detail if it's the deleted one
      if (selectedMessage && selectedMessage._id === id) {
        setSelectedMessage(null);
      }

    } catch (error) {
      console.error('Error deleting message:', error);
    }
  };

  // View message details
  const viewMessage = (message) => {
    setSelectedMessage(message);

    // If message is unread, mark it as read
    if (!message.isRead) {
      markAsRead(message._id);
    }
  };

  // Close message detail
  const closeMessageDetail = () => {
    setSelectedMessage(null);
  };

  // Format date
  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) return <div className="loading">Loading messages...</div>;
  if (error) return <div className="error-message">Error: {error}</div>;

  return (
    <div className="admin-messages-page">
      <h1>Messages</h1>

      <div className="messages-container">
        <div className="messages-list">
          {messages.length === 0 ? (
            <div className="no-messages">No messages yet</div>
          ) : (
            messages.map(message => (
              <div 
                key={message._id} 
                className={`message-item ${message.isRead ? '' : 'unread'} ${selectedMessage && selectedMessage._id === message._id ? 'selected' : ''}`}
                onClick={() => viewMessage(message)}
              >
                <div className="message-sender">{message.name}</div>
                <div className="message-subject">{message.subject}</div>
                <div className="message-preview">
                  {message.message.length > 60 
                    ? `${message.message.substring(0, 60)}...` 
                    : message.message}
                </div>
                <div className="message-date">{formatDate(message.createdAt)}</div>
              </div>
            ))
          )}
        </div>

        {selectedMessage && (
          <div className="message-detail">
            <div className="message-detail-header">
              <button className="close-button" onClick={closeMessageDetail}>×</button>
              <h2>{selectedMessage.subject}</h2>
              <div className="message-meta">
                <span className="from">From: {selectedMessage.name} &lt;{selectedMessage.email}&gt;</span>
                <span className="date">Date: {formatDate(selectedMessage.createdAt)}</span>
              </div>
            </div>

            <div className="message-body">
              {selectedMessage.message}
            </div>

            <div className="message-actions">
              <button 
                className="delete-button"
                onClick={() => deleteMessage(selectedMessage._id)}
              >
                Delete
              </button>
              <a 
                href={`mailto:${selectedMessage.email}?subject=Re: ${selectedMessage.subject}`}
                className="reply-button"
              >
                Reply
              </a>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default MessagesPage;
