import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import '../styles/MessagesPage.css';

const MessagesPage = () => {
  const [messages, setMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    // Check if user is authenticated
    const token = localStorage.getItem('adminToken');
    if (!token) {
      navigate('/admin/login');
      return;
    }

    // Fetch messages
    const fetchMessages = async () => {
      try {
        const response = await axios.get('http://localhost:8000/api/messages', {
          headers: {
            Authorization: `Bearer ${token}`
          }
        });
        setMessages(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching messages:', err);
        setError('Failed to fetch messages. Please try again.');
        setLoading(false);

        // If unauthorized, redirect to login
        if (err.response?.status === 401) {
          localStorage.removeItem('adminToken');
          navigate('/admin/login');
        }
      }
    };

    fetchMessages();
  }, [navigate]);

  const handleLogout = () => {
    localStorage.removeItem('adminToken');
    navigate('/admin/login');
  };

  const formatDate = (dateString) => {
    const options = { 
      year: 'numeric', 
      month: 'long', 
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="messages-container">
        <div className="loading-spinner">
          <div className="spinner"></div>
          <p>Loading messages...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="messages-container">
      <header className="messages-header">
        <h1>Admin Messages</h1>
        <button onClick={handleLogout} className="logout-button">Logout</button>
      </header>

      {error && (
        <div className="error-banner">
          <p>{error}</p>
        </div>
      )}

      <div className="messages-count">
        <p>Total Messages: <span>{messages.length}</span></p>
      </div>

      {messages.length === 0 ? (
        <div className="no-messages">
          <p>No messages found.</p>
        </div>
      ) : (
        <div className="messages-list">
          {messages.map((message) => (
            <div key={message._id} className="message-card">
              <div className="message-header">
                <h3>{message.name}</h3>
                <span className="message-date">{formatDate(message.createdAt)}</span>
              </div>

              <div className="message-contact">
                <p><strong>Email:</strong> {message.email}</p>
                {message.phone && <p><strong>Phone:</strong> {message.phone}</p>}
              </div>

              <div className="message-content">
                <p>{message.message}</p>
              </div>

              <div className="message-status">
                <span className={`status-badge ${message.read ? 'read' : 'unread'}`}>
                  {message.read ? 'Read' : 'Unread'}
                </span>
              </div>

              <div className="message-actions">
                <button 
                  className="mark-read-btn"
                  onClick={async () => {
                    try {
                      const token = localStorage.getItem('adminToken');
                      await axios.patch(
                        `http://localhost:8000/api/messages/${message._id}`,
                        { read: !message.read },
                        {
                          headers: {
                            Authorization: `Bearer ${token}`
                          }
                        }
                      );

                      // Update local state
                      setMessages(messages.map(msg => 
                        msg._id === message._id ? {...msg, read: !msg.read} : msg
                      ));
                    } catch (err) {
                      console.error('Error updating message:', err);
                    }
                  }}
                >
                  Mark as {message.read ? 'Unread' : 'Read'}
                </button>

                <button 
                  className="delete-btn"
                  onClick={async () => {
                    if (window.confirm('Are you sure you want to delete this message?')) {
                      try {
                        const token = localStorage.getItem('adminToken');
                        await axios.delete(
                          `http://localhost:8000/api/messages/${message._id}`,
                          {
                            headers: {
                              Authorization: `Bearer ${token}`
                            }
                          }
                        );

                        // Remove from local state
                        setMessages(messages.filter(msg => msg._id !== message._id));
                      } catch (err) {
                        console.error('Error deleting message:', err);
                      }
                    }
                  }}
                >
                  Delete
                </button>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

export default MessagesPage;
