import React, { useState, useEffect, useRef } from 'react';
import '../styles/About.css';

const About = () => {
  const [activeSection, setActiveSection] = useState('mission');
  const [scrollProgress, setScrollProgress] = useState(0);
  const contentRef = useRef(null);

  useEffect(() => {
    const handleScroll = () => {
      if (contentRef.current) {
        const totalHeight = contentRef.current.scrollHeight - window.innerHeight;
        const progress = (window.scrollY / totalHeight) * 100;
        setScrollProgress(Math.min(Math.max(progress, 0), 100));
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  return (
    <div className="about-wrapper">
      {/* Scroll Progress Bar */}
      <div 
        className="scroll-progress-bar" 
        style={{ width: `${scrollProgress}%` }}
      ></div>

      {/* Hero Section */}
      <section className="about-hero">
        <div className="hero-content">
          <div className="hero-text">
            <h1>Transforming Digital <span className="gradient-text">Possibilities</span></h1>
            <p>We're a passionate team dedicated to empowering businesses through innovative technology solutions</p>
            <div className="hero-stats">
              <div className="stat">
                <span className="stat-number">200+</span>
                <span className="stat-label">Clients</span>
              </div>
              <div className="stat">
                <span className="stat-number">98%</span>
                <span className="stat-label">Satisfaction</span>
              </div>
              <div className="stat">
                <span className="stat-number">24/7</span>
                <span className="stat-label">Support</span>
              </div>
            </div>
          </div>
          <div className="hero-visual">
            <div className="hero-shapes">
              <div className="shape shape-1"></div>
              <div className="shape shape-2"></div>
              <div className="shape shape-3"></div>
            </div>
          </div>
        </div>
      </section>

      {/* Story Navigation */}
      <section className="story-navigation">
        <div className="nav-buttons">
          {[
            { id: 'mission', label: 'Our Mission' },
            { id: 'team', label: 'Our Team' },
            { id: 'journey', label: 'Our Journey' }
          ].map((section) => (
            <button 
              key={section.id}
              className={`nav-button ${activeSection === section.id ? 'active' : ''}`}
              onClick={() => setActiveSection(section.id)}
            >
              {section.label}
            </button>
          ))}
        </div>
      </section>

      {/* Dynamic Content Sections */}
      <section ref={contentRef} className="about-content">
        {activeSection === 'mission' && (
          <div className="mission-section">
            <div className="section-header">
              <h2>Our <span className="gradient-text">Mission</span></h2>
              <p>Bridging technology and business growth through innovative solutions</p>
            </div>
            <div className="mission-grid">
              {[
                {
                  icon: "🚀",
                  title: "Innovation",
                  description: "Pushing boundaries of digital transformation",
                  gradient: "linear-gradient(135deg, #6366F1, #3B82F6)"
                },
                {
                  icon: "💡",
                  title: "Creativity",
                  description: "Solving complex problems with unique approaches",
                  gradient: "linear-gradient(135deg, #EC4899, #8B5CF6)"
                },
                {
                  icon: "🤝",
                  title: "Partnership",
                  description: "Collaborative growth with our clients",
                  gradient: "linear-gradient(135deg, #10B981, #14B8A6)"
                }
              ].map((value, index) => (
                <div 
                  key={index} 
                  className="mission-card"
                  style={{ 
                    background: value.gradient,
                    backgroundClip: 'border-box'
                  }}
                >
                  <div className="mission-icon">{value.icon}</div>
                  <h3>{value.title}</h3>
                  <p>{value.description}</p>
                </div>
              ))}
            </div>
          </div>
        )}

