import React, { useState, useCallback } from 'react';
import '../styles/Contact.css';

const Contact = () => {
  // Form data state
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    subject: '',
    message: '',
    service: ''
  });

  // Submission states
  const [submissionStatus, setSubmissionStatus] = useState({
    isSubmitting: false,
    success: false,
    error: false,
    message: ''
  });

  // Handle input changes with memoization
  const handleChange = useCallback((e) => {
    const { name, value } = e.target;
    setFormData(prevData => ({
      ...prevData,
      [name]: value
    }));

    // Reset error state when user starts typing
    if (submissionStatus.error) {
      setSubmissionStatus(prev => ({
        ...prev,
        error: false,
        message: ''
      }));
    }
  }, [submissionStatus.error]);

  // Validate form data
  const validateForm = () => {
    const { name, email, subject, message, service } = formData;
    const requiredFields = [name, email, subject, message, service];

    return requiredFields.every(field => field.trim() !== '');
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    // Validate form
    if (!validateForm()) {
      setSubmissionStatus({
        isSubmitting: false,
        success: false,
        error: true,
        message: 'Please fill in all required fields.'
      });
      return;
    }

    // Start submission process
    setSubmissionStatus({
      isSubmitting: true,
      success: false,
      error: false,
      message: ''
    });

    try {
      // Attempt to send message
      const response = await fetch('/api/messages', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      const data = await response.json();

      // Handle response
      if (response.ok) {
        // Success scenario
        setSubmissionStatus({
          isSubmitting: false,
          success: true,
          error: false,
          message: 'Message sent successfully!'
        });

        // Reset form
        setFormData({
          name: '',
          email: '',
          phone: '',
          subject: '',
          message: '',
          service: ''
        });

        // Auto-hide success message
        setTimeout(() => {
          setSubmissionStatus(prev => ({
            ...prev,
            success: false,
            message: ''
          }));
        }, 5000);
      } else {
        // Server-side error
        throw new Error(data.message || 'Something went wrong');
      }
    } catch (error) {
      // Handle network or submission errors
      setSubmissionStatus({
        isSubmitting: false,
        success: false,
        error: true,
        message: error.message || 'Failed to send message. Please try again.'
      });

      // Log error for debugging
      console.error('Submission Error:', error);
    }
  };

  return (
    <div className="contact-page">
      {/* Hero Section */}
      <section className="contact-hero">
        <div className="container">
          <div className="hero-content">
            <h1>Let's Work <span className="gradient-text">Together</span></h1>
            <p>We're excited to hear about your project. Fill out the form below and we'll get back to you within 24 hours.</p>
          </div>
        </div>
        <div className="hero-shape"></div>
      </section>

      {/* Main Contact Section */}
      <section className="main-contact-section">
        <div className="container">
          <div className="contact-grid">
            {/* Contact Information */}
            <div className="contact-info">
              <div className="info-card">
                <h2>Contact Information</h2>
                <p className="info-subtitle">We're here to help with any questions you have</p>

                <div className="info-items">
                  <div className="info-item">
                    <div className="info-icon email-icon">
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 8l7.89 5.26a2 2 0 002.22 0L21 8M5 19h14a2 2 0 002-2V7a2 2 0 00-2-2H5a2 2 0 00-2 2v10a2 2 0 002 2z" />
                      </svg>
                    </div>
                    <div className="info-content">
                      <h3>Email Us</h3>
                      <a href="mailto:hello@seraune.com">hello@seraune.com</a>
                    </div>
                  </div>

                  <div className="info-item">
                    <div className="info-icon phone-icon">
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 5a2 2 0 012-2h3.28a1 1 0 01.948.684l1.498 4.493a1 1 0 01-.502 1.21l-2.257 1.13a11.042 11.042 0 005.516 5.516l1.13-2.257a1 1 0 011.21-.502l4.493 1.498a1 1 0 01.684.949V19a2 2 0 01-2 2h-1C9.716 21 3 14.284 3 6V5z" />
                      </svg>
                    </div>
                    <div className="info-content">
                      <h3>Call Us</h3>
                      <a href="tel:+15555555555">(555) 555-5555</a>
                    </div>
                  </div>

                  <div className="info-item">
                    <div className="info-icon location-icon">
                      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" />
                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" />
                      </svg>
                    </div>
                    <div className="info-content">
                      <h3>Visit Us</h3>
                      <p>123 Tech Street<br />San Francisco, CA 94107</p>
                    </div>
                  </div>
                </div>

                <div className="social-links">
                  <h3>Connect With Us</h3>
                  <div className="social-icons">
                    <a href="https://twitter.com" target="_blank" rel="noopener noreferrer" className="social-icon twitter">
                      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M24 4.557c-.883.392-1.832.656-2.828.775 1.017-.609 1.798-1.574 2.165-2.724-.951.564-2.005.974-3.127 1.195-.897-.957-2.178-1.555-3.594-1.555-3.179 0-5.515 2.966-4.797 6.045-4.091-.205-7.719-2.165-10.148-5.144-1.29 2.213-.669 5.108 1.523 6.574-.806-.026-1.566-.247-2.229-.616-.054 2.281 1.581 4.415 3.949 4.89-.693.188-1.452.232-2.224.084.626 1.956 2.444 3.379 4.6 3.419-2.07 1.623-4.678 2.348-7.29 2.04 2.179 1.397 4.768 2.212 7.548 2.212 9.142 0 14.307-7.721 13.995-14.646.962-.695 1.797-1.562 2.457-2.549z"/>
                      </svg>
                    </a>
                    <a href="https://facebook.com" target="_blank" rel="noopener noreferrer" className="social-icon facebook">
                      <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="currentColor">
                        <path d="M9 8h-3v4h3v12h5v-12h3.642l.358-4h-4v-1.667c0-.955.192-1.333 1.115-1.333h2.885v-5h-3.808c-3.596 0-5.192 1.583-5.192 4.615v3.385z"/>
                      </svg>
